package com.sleepwalker.utils;import java.util.ArrayList;import java.util.Arrays;import java.util.HashSet;import java.util.List;import java.util.Set;import org.apache.commons.lang.StringUtils;/** * 用户权限工具类 */public class UserAuthUtil {    public static List<String> getAuthList(String userAuth) {        if (StringUtils.isNotBlank(userAuth)) {            return Arrays.asList(userAuth.split(","));        }        return null;    }    public static List<String> addNewAuth(List<String> authList, String newAuth) {        if (authList == null) {            authList = new ArrayList<>();        }        Set<String> authSet = new HashSet<>(authList);        authSet.add(newAuth);        return new ArrayList<>(authSet);    }    public static List<String> removeAuth(List<String> authList, String beRemovedAuth) {        if (StringUtils.isNotBlank(beRemovedAuth) && authList != null) {            Set<String> authSet = new HashSet<>(authList);            authSet.remove(beRemovedAuth);            return new ArrayList<>(authSet);        }        return authList;    }    public static void main(String[] args) {        List<String> authList = new ArrayList<>();        authList.add("ROLE_USER");        authList.add("ROLE_ADMIN");        authList.add("ROLE_CONSOLE");        authList.add("ROLE_CONSOLE");        authList.add("ROLE_CONSOLE");        authList = removeAuth(authList, "ROLE_CONSOLE");        for (String auth : authList) {            System.out.println(auth);        }    }}